#!/bin/bash

echo \"======================================\"\necho \"Flight Analytics - GitHub Setup\"\necho \"======================================\"\necho \"\"\n\n# Check if git is initialized\nif [ ! -d \".git\" ]; then\n    echo \"Initializing git repository...\"\n    git init\n    echo \"Git initialized!\"\nelse\n    echo \"Git repository already initialized.\"\nfi\n\necho \"\"\necho \"Setting up .gitignore...\"\n\n# Add common ignores if not present\nif [ ! -f \".gitignore\" ]; then\n    echo \"Creating .gitignore...\"\n    cat > .gitignore << 'EOF'\n# Python\n__pycache__/\n*.py[cod]\nvenv/\n.venv/\n\n# Node\nnode_modules/\nyarn-error.log*\n\n# Environment\n.env\n.env.local\n\n# Logs\n*.log\nlogs/\n\n# OS\n.DS_Store\nEOF\n    echo \".gitignore created!\"\nfi\n\necho \"\"\necho \"Staging files for commit...\"\ngit add .\n\necho \"\"\necho \"Creating initial commit...\"\ngit commit -m \"Initial commit: Flight Analytics & Recommendation System\n\nFeatures:\n- OpenFlights data ingestion (7,698 airports, 6,162 airlines, 67,240 routes)\n- FastAPI backend with analytics and recommendation APIs\n- React frontend with modern UI\n- TF-IDF vector embeddings for route similarity\n- MongoDB with vector storage\n- Fully functional and deployed\"\n\necho \"\"\necho \"======================================\"\necho \"Next Steps:\"\necho \"======================================\"\necho \"1. Create a new repository on GitHub\"\necho \"2. Copy the repository URL\"\necho \"3. Run these commands:\"\necho \"\"\necho \"   git remote add origin <your-github-repo-url>\"\necho \"   git branch -M main\"\necho \"   git push -u origin main\"\necho \"\"\necho \"Repository is ready to push to GitHub!\"\necho \"======================================\"\n